import React, { useState } from 'react';
import StockQuote from '../components/StockQuote';
import StockChart from '../components/StockChart';
import { StockQuote as StockQuoteType, StockChartData } from '../types/stock';
import { getStockQuote, getStockChartData } from '../services/stockService';

const Home: React.FC = () => {
  const [symbol, setSymbol] = useState<string>('');
  const [stock, setStock] = useState<StockQuoteType>({} as StockQuoteType);
  const [chartData, setChartData] = useState<StockChartData[]>([]);
  const [error, setError] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(false);

  const handleSearch = async () => {
    if (!symbol) return;

    try {
      setLoading(true);
      setError('');
      setStock({} as StockQuoteType);
      setChartData([]);

      const quoteData = await getStockQuote(symbol);
      setStock(quoteData);

      const graphData = await getStockChartData(symbol);
      setChartData(graphData);
    } catch (err: any) {
      if (err.response && err.response.status === 404) {
        setError('找不到指定的股票代码...');
      } else {
        setError('获取数据时出错...');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div className="container">
      <h1 className="mb-4">股票预测应用</h1>
      <div className="row mb-4">
        <div className="col-md-10">
          <input
            type="text"
            className="form-control"
            value={symbol}
            onChange={(e) => setSymbol(e.target.value.toUpperCase())}
            onKeyPress={handleKeyPress}
            placeholder="输入股票代码..."
            autoFocus
          />
        </div>
        <div className="col-md-2">
          <button
            className="btn btn-primary full-width"
            onClick={handleSearch}
            disabled={loading}
          >
            {loading ? (
              <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
            ) : (
              '搜索'
            )}
          </button>
        </div>
      </div>

      {error && (
        <div className="alert alert-danger" role="alert">
          <strong>错误!</strong> {error}
        </div>
      )}

      <div className="row">
        <div className="col-md-5">
          {stock.symbol && <StockQuote stock={stock} />}
        </div>
        <div className="col-md-7">
          {chartData.length > 0 && (
            <StockChart 
              chartData={chartData} 
              companyName={stock.companyName} 
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default Home;
